<script setup lang="ts">
import {getRegionList} from "~/services/region";

const {t} = useI18n()
const props = defineProps<{ analyticsList?: [] }>()
const hoverValue = ref('')
const placeId = ref('')
const spinning = ref(false)
const regionsList = ref([])
const isCount = ref(null)
const changeHoverValue = (hvalue: any) => {
  if (hvalue !== null && hvalue.target !== null) {
    const title = hvalue.target.attributes['title']
    hoverValue.value = title.value
    const id = hvalue.target.attributes['id']
    placeId.value = id.value
    isCount.value = props.analyticsList.find((item: any) => item.id === placeId.value)

  }
}

const fetchData = async () => {
  spinning.value = true;
  try {
    const {data} = await getRegionList();
    regionsList.value = data;
  } catch (err) {
    return err;
  }finally {
    spinning.value = false;
  }
};
fetchData()
</script>

